                            } else {
                                if ( $block['text'] !== '' ) {
                                    $block['text'] .= "\n";
                                }

                                $block['text'] .= $line;
                            }

                            continue 2;
                        }

                        break;

                    case 'markup':

                        if ( ! isset( $block['closed'] ) ) {
                            if ( strpos( $line, $block['start'] ) !== false ) { # opening tag
                                $block['depth'] ++;
                            }

                            if ( strpos( $line, $block['end'] ) !== false ) { # closing tag
                                if ( $block['depth'] > 0 ) {
                                    $block['depth'] --;
                                } else {
                                    $block['closed'] = true;
                                }
                            }

                            $block['text'] .= "\n" . $line;

                            continue 2;
                        }

                        break;
                }

                # ~

                $indentation = 0;

                while ( isset( $line[ $indentation ] ) and $line[ $indentation ] === ' ' ) {
